#include "ft_shmup.h"

void	update_bg(char **bg)
{
	static int	offset = 0;
	int			i;

	i = 0;
	offset++;
	if (offset == 30 * 197)
		offset = 0;
	while (i < 25)
	{
		move(i, 0);
		printw("%.80s", &(bg[i][offset/30]));
		i++;
	}
	refresh();
}

